import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {

  constructor(){
    super()
    this.state = {
      gameState: 0, // 0 =  not started, 1 = started, 2 = ended
      round: 0,
      question: "",
      rightAnswer: "",
      answers: "",
      rightAnswerIndex: 999,
      selectedAnswer: 999,
      isAnswerSelected: false
    }

    this.startGame = this.startGame.bind(this)
  } // end constructor()

  render(){
    return (
      <div className="App">
        <header className="App-header">
          Who Wants to Be a Millionaire?
          <img src={logo} className="App-logo" alt="logo" />
        </header>

        {
          this.gameState !== 1 ? <button onClick={this.startGame}>NEW GAME</button> : this.props.textOrHtml
        }

      </div>
    )
  } // end render()

  startGame(){
    this.setState({
      gameState: 1 // started
    })
  } // end startGame()


}

export default App;
