import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {

  constructor(){
    super()
    this.state = {
      gameState: 0, // 0 =  not started, 1 = started, 2 = ended
      round: 0,
      question: "",
      rightAnswer: "",
      answers: [],
      rightAnswerIndex: 999,
      selectedAnswer: 999,
      isAnswerSelected: false,
      difficulty: "",
      category: "",
      jokerInfo: ["A", "B", "C", "D"],
      jokerAvailable: [false, false, false]
    }

    this.startGame = this.startGame.bind(this)
    this.nextQuestion = this.nextQuestion.bind(this)
    this.shuffleQuestions = this.shuffleQuestions.bind(this)
    this.selectAnswer = this.selectAnswer.bind(this)
  } // end constructor()

  render(){
    return (
      <div className="App">
        <header className="App-header">
          Who Wants to Be a Millionaire?
          <img src={logo} className="App-logo" alt="logo" />
        </header>

        {
          this.state.gameState !== 1 ? <button onClick={this.startGame}>NEW GAME</button> : this.props.textOrHtml
        }

        {
          this.state.gameState !== 0 ?
          <div>

            Category: {this.state.category} ({this.state.difficulty})
            <br/>
            {this.state.question}
            <br/>
            <br/>

            <button>A) {this.state.answers[0]}</button>
            <button>B) {this.state.answers[1]}</button>
            <br/>
            <button>C) {this.state.answers[2]}</button>
            <button>D) {this.state.answers[3]}</button>

            {
              this.state.gameState === 1 && this.state.round <= 15 && this.state.isAnswerSelected
              ?
              <button>Next Question</button>
              :
              this.props.textOrHtml
            }

          </div>
          :
          this.props.textOrHtml
        }

      </div>
    )
  } // end render()

  startGame(){
    this.setState({
      gameState: 1, // started
      jokerAvailable: [false, false, false]
    })

    this.nextQuestion()
  } // end startGame()

  nextQuestion(){
    let round = this.state.round
    let apiURL = ""
    if(round < 5) {
      apiURL = "https://opentdb.com/api.php?amount=1&difficulty=easy&type=multiple"
    } else if(round < 10) {
      apiURL = "https://opentdb.com/api.php?amount=1&difficulty=medium&type=multiple"
    } else {
      apiURL = "https://opentdb.com/api.php?amount=1&difficulty=hard&type=multiple"
    }

    fetch(apiURL)
      .then(response => response.json())
      .then(data => this.setState({
        rightAnswer: data.results[0].correct_answer,
        answers: data.results[0].incorrect_answers,
        question: data.results[0].question,
        difficulty: data.results[0].difficulty,
        category: data.results[0].category,
        rightAnswerIndex: 999,
        selectedAnswer: 999,
        isAnswerSelected: false
      }))
      .then(this.shuffleQuestions)
  } // end nextQuestion()

  shuffleQuestions(){
    const randomNumber = Math.floor(Math.random() * 4) + 1
    let shuffledAnswers = this.state.answers
    shuffledAnswers.splice(randomNumber-1, 0, this.state.rightAnswer)
    this.setState({
      rightAnswerIndex: randomNumber-1,
      answers: shuffledAnswers
    })
    console.log("Right answer: " + this.state.rightAnswer + "\nOn Index: " + this.state.rightAnswerIndex)
  } // end shuffleQuestions()

  selectAnswer(index){

  } // end selectedAnswer()

} // end App{}

export default App;
